import React, { useState } from "react";
import { useNavigate } from "react-router";
import ContainerText from './ContainerText';
import TextButton from './TextButton';
import TextField from "./TextField";

// export details of record to other file
export default function Create() {
  const [form, setForm] = useState({
    name: "",
    position: "",
    level: "",
  });
  const navigate = useNavigate();

  // These methods will update the state properties using spread notation.
  // ...var lets you reference multiple variables when you aren't sure of the amount being passed
  function updateForm(value) {
    return setForm((prev) => {
      return { ...prev, ...value };
    });
  }

  // This function will handle the submission.
  async function onSubmit(e) {
    e.preventDefault();

    // When a post request is sent to the create url, we'll add a new record to the database.
    const newPerson = { ...form };

    await fetch("http://localhost:5000/record/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPerson),
    })
      .catch(error => {
        window.alert(error);
        return;
      });

    setForm({ name: "", position: "", level: "" });
    navigate("/");
  }

  // This following section will display the form that takes the input from the user.
  return (
    <div className="space-y-6 items-center">
      <ContainerText inputHead="Create New Record"
        inputText={
          <form onSubmit={onSubmit}>
            <div className="form-group">
              <TextField type="text" element_id="name" element_text="Name" placeholder="First Name" value_input={form.name} onChange={(e) => updateForm({ name: e.target.value })}/>
              
            </div>
            <div className="form-group">
              <label htmlFor="position">Position</label>
              <input
                type="text"
                className="form-control"
                id="position"
                value={form.position}
                onChange={(e) => updateForm({ position: e.target.value })}
              />
            </div>
            <div className="form-group">
              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  name="positionOptions"
                  id="positionIntern"
                  value="Intern"
                  checked={form.level === "Intern"}
                  onChange={(e) => updateForm({ level: e.target.value })}
                />
                <label htmlFor="positionIntern" className="form-check-label">Intern</label>
              </div>
              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  name="positionOptions"
                  id="positionJunior"
                  value="Junior"
                  checked={form.level === "Junior"}
                  onChange={(e) => updateForm({ level: e.target.value })}
                />
                <label htmlFor="positionJunior" className="form-check-label">Junior</label>
              </div>
              <div className="form-check form-check-inline">
                <input
                  className="form-check-input"
                  type="radio"
                  name="positionOptions"
                  id="positionSenior"
                  value="Senior"
                  checked={form.level === "Senior"}
                  onChange={(e) => updateForm({ level: e.target.value })}
                />
                <label htmlFor="positionSenior" className="form-check-label">Senior</label>

              </div>
            </div>
            <div className="form-group">
              <TextButton type="submit" prompt="Create user"/>
            </div>
          </form>
        } />
    </div>
  );
}
